import React, { useState, useEffect } from 'react';
import { ArrowLeft, CheckCircle, AlertCircle, FileText } from 'lucide-react';

interface CreateClaimFormProps {
  onBack: () => void;
}

interface ClaimFormData {
  claimID: string;
  patientName: string;
  patientMobile: string;
  hospitalCity: string;
  hospitalState: string;
  patientLanguage: string;
}

interface ClaimFormErrors {
  [key: string]: string;
}

type SubmitStatus = {
  type: 'success' | 'error';
  message: string;
} | null;

const CreateClaimForm: React.FC<CreateClaimFormProps> = ({ onBack }) => {
  const [formData, setFormData] = useState<ClaimFormData>({
    claimID: '',
    patientName: '',
    patientMobile: '',
    hospitalCity: '',
    hospitalState: '',
    patientLanguage: '',
  });

  const [errors, setErrors] = useState<ClaimFormErrors>({});
  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
  const [submitStatus, setSubmitStatus] = useState<SubmitStatus>(null);

  useEffect(() => {
    // Generate random claim ID on mount
    setFormData(prev => ({
      ...prev,
      claimID: `CLM-${Math.floor(100000 + Math.random() * 900000)}`
    }));
  }, []);

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    const validFiles = files.filter((file) => {
      const validTypes = [
        'application/pdf',
        'application/zip',
        'application/x-zip-compressed',
      ];
      return (
        validTypes.includes(file.type) ||
        file.name.toLowerCase().endsWith('.pdf') ||
        file.name.toLowerCase().endsWith('.zip')
      );
    });

    if (validFiles.length !== files.length) {
      setErrors(prev => ({
        ...prev,
        files: 'Only PDF and ZIP files are allowed.',
      }));
    }

    setUploadedFiles(prev => [...prev, ...validFiles]);
    setErrors(prev => ({ ...prev, files: '' }));
  };

  const removeFile = (index: number) => {
    setUploadedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const validateForm = (): ClaimFormErrors => {
    const newErrors: ClaimFormErrors = {};

    if (!formData.patientName.trim()) {
      newErrors.patientName = 'Patient name is required';
    }

    if (!formData.patientMobile.trim()) {
      newErrors.patientMobile = 'Patient mobile number is required';
    } else if (!/^\+?[\d\s\-\(\)]{10,}$/.test(formData.patientMobile)) {
      newErrors.patientMobile = 'Please enter a valid mobile number';
    }

    if (!formData.hospitalCity.trim()) {
      newErrors.hospitalCity = 'Hospital city is required';
    }

    if (!formData.hospitalState) {
      newErrors.hospitalState = 'Hospital state is required';
    }

    if (!formData.patientLanguage) {
      newErrors.patientLanguage = 'Patient language is required';
    }

    if (uploadedFiles.length === 0) {
      newErrors.files = 'At least one file must be uploaded';
    }

    return newErrors;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    try {
      const formDataToSubmit = new FormData();

      // Append form fields
      for (const [key, value] of Object.entries(formData)) {
        formDataToSubmit.append(key, value);
      }

      // Append files
      uploadedFiles.forEach((file, index) => {
        formDataToSubmit.append(`file_${index}`, file);
      });

      const response = await fetch('/api/claims', {
        method: 'POST',
        body: formDataToSubmit,
      });

      if (response.ok) {
        setSubmitStatus({ type: 'success', message: 'Claim created successfully!' });

        // Reset form
        setTimeout(() => {
          setSubmitStatus(null);
          setUploadedFiles([]);
          setErrors({});
          setFormData({
            claimID: `CLM-${Math.floor(100000 + Math.random() * 900000)}`,
            patientName: '',
            patientMobile: '',
            hospitalCity: '',
            hospitalState: '',
            patientLanguage: '',
          });
        }, 3000);
      } else {
        setSubmitStatus({
          type: 'error',
          message: 'Failed to create claim. Please try again.',
        });
      }
    } catch (error) {
      console.error('Error submitting claim:', error);
      setSubmitStatus({
        type: 'error',
        message: 'Something went wrong. Please try again later.',
      });
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <form onSubmit={handleSubmit} className="p-6 max-w-3xl mx-auto">
      <button
        type="button"
        onClick={onBack}
        className="flex items-center mb-4 text-purple-400 hover:text-purple-300"
      >
        <ArrowLeft className="w-5 h-5 mr-2" />
        Back
      </button>

      <h2 className="text-2xl font-semibold text-white mb-6">Create Claim</h2>

      {/* Display submit status */}
      {submitStatus && (
        <div className={`mb-6 p-4 rounded-xl border backdrop-blur-md ${
          submitStatus.type === 'success'
            ? 'bg-green-500/20 border-green-500/50 text-green-200'
            : 'bg-red-500/20 border-red-500/50 text-red-200'
        }`}>
          <div className="flex items-center space-x-3">
            {submitStatus.type === 'success' ? (
              <CheckCircle className="w-5 h-5" />
            ) : (
              <AlertCircle className="w-5 h-5" />
            )}
            <span className="font-medium">{submitStatus.message}</span>
          </div>
        </div>
      )}

      {/* Form fields */}
      {/* Example: Patient Name */}
      <div className="mb-4">
        <label className="block text-white font-medium mb-1">Patient Name</label>
        <input
          name="patientName"
          value={formData.patientName}
          onChange={handleInputChange}
          placeholder="Enter patient's full name"
          className={`w-full px-4 py-3 bg-slate-700/50 border rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-200 ${
            errors.patientName ? 'border-red-500' : 'border-slate-600'
          }`}
        />
        {errors.patientName && <p className="text-red-400 text-xs mt-1">{errors.patientName}</p>}
      </div>

      {/* Repeat similar blocks for other fields: patientMobile, hospitalCity, hospitalState, patientLanguage */}

      {/* File Upload */}
      <div className="mb-4">
        <label className="block text-white font-medium mb-1">Upload Files</label>
        <input
          type="file"
          multiple
          onChange={handleFileUpload}
          className="text-white"
          accept=".pdf,.zip"
        />
        {errors.files && <p className="text-red-400 text-xs mt-1">{errors.files}</p>}

        {/* Uploaded files list */}
        {uploadedFiles.length > 0 && (
          <ul className="mt-2 space-y-2">
            {uploadedFiles.map((file, index) => (
              <li key={index} className="flex items-center justify-between bg-slate-700/50 px-4 py-2 rounded-xl text-white">
                <div className="flex items-center space-x-3">
                  <FileText className="w-5 h-5 text-purple-400" />
                  <div>
                    <p className="text-sm font-medium">{file.name}</p>
                    <p className="text-xs text-gray-400">{formatFileSize(file.size)}</p>
                  </div>
                </div>
                <button
                  type="button"
                  onClick={() => removeFile(index)}
                  className="text-red-400 hover:text-red-300 transition-colors text-sm"
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>

      <button
        type="submit"
        className="mt-6 w-full py-3 bg-purple-600 text-white font-medium rounded-xl hover:bg-purple-500 transition-all duration-200"
      >
        Submit Claim
      </button>
    </form>
  );
};

export default CreateClaimForm;
