import React, { useState, useEffect } from 'react';
import {
  Video,
  Phone,
  FileText,
  Eye,
  Check,
  ChevronRight,
  ExternalLink,
  MessageSquare,
  Copy,
  Users
} from 'lucide-react';
import { videoCallAPI, formsAPI, geolocationAPI } from '../services/api';
import { useNavigate } from 'react-router-dom';

const MultiStepForm = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [completedSteps, setCompletedSteps] = useState<number[]>([]);
  const [formData, setFormData] = useState({
    // Patient Details
    claimNumber: '',
    patientName: '',
    doj: '',
    mobileNumber: '',
    email: '',
    policyNumber: '',
    product: '',
    relationship: '',
    insuredName: '',

    // Hospital Details
    hospitalName: '',
    hospitalLocation: '',
    hospitalState: '',
    dod: '',
    ntId: '',
    hospitalStatus: '',
    diagnosisDetails: '',

    // Additional Information
    message: ''
  });

  const [videoCallStatus, setVideoCallStatus] = useState('idle'); // idle, links-generated, pending, completed
  const [sessionId, setSessionId] = useState<string | null>(null);
  const [meetingLinks, setMeetingLinks] = useState<{
    patientLink: string;
    doctorLink: string;
  } | null>(null);
  const [geolocation, setGeolocation] = useState<{
    latitude: number;
    longitude: number;
    accuracy: number;
  } | null>(null);
  const [smsStatus, setSmsStatus] = useState('idle'); // idle, sending, sent, error

  const steps = [
    { id: 1, title: 'Video Meeting', icon: Video, description: 'Generate & join video call' },
    { id: 2, title: 'Claim Details', icon: FileText, description: 'Enter claim information' },
    { id: 3, title: 'Review & Submit', icon: Eye, description: 'Final review and submission' }
  ];

  // Capture geolocation on component mount
  useEffect(() => {
    const captureGeolocation = async () => {
      try {
        const position = await geolocationAPI.getCurrentPosition();
        setGeolocation(position);
      } catch (error) {
        console.error('Error getting geolocation:', error);
      }
    };

    captureGeolocation();
  }, []);

  const completeStep = React.useCallback(
    (stepId: number) => {
      if (!completedSteps.includes(stepId)) {
        setCompletedSteps((prev) => [...prev, stepId]);
      }
    },
    [completedSteps]
  );

  useEffect(() => {
    let pollInterval: NodeJS.Timeout;

    if (videoCallStatus === 'pending' && sessionId) {
      pollInterval = setInterval(async () => {
        try {
          const data = await videoCallAPI.getStatus(sessionId);

          if (data.status === 'completed') {
            setVideoCallStatus('completed');
            completeStep(1);
            clearInterval(pollInterval);
          }
        } catch (error) {
          console.error('Error polling video call status:', error);
        }
      }, 2000);
    }

    return () => {
      if (pollInterval) {
        clearInterval(pollInterval);
      }
    };
  }, [videoCallStatus, sessionId, completeStep]);

  const canAccessStep = (stepId: number) => {
    if (stepId === 1) return true;
    return completedSteps.includes(stepId - 1);
  };

  // Generate meeting links
  const generateMeetingLinks = async () => {
    try {
      const response = await videoCallAPI.create({
        claimId: 'CLM-2025-8847',
        patientName: 'John Smith',
        procedure: 'Insurance Claim Verification'
      });

      if (response.success) {
        setSessionId(response.sessionId);
        setMeetingLinks({
          patientLink: `${window.location.origin}/meeting/patient?sessionId=${response.sessionId}&roomName=${response.roomName}`,
          doctorLink: `${window.location.origin}/meeting/doctor?sessionId=${response.sessionId}&roomName=${response.roomName}`
        });
        setVideoCallStatus('links-generated');
      } else {
        alert('Failed to generate meeting links. Please try again.');
      }
    } catch (error) {
      console.error('Error generating meeting links:', error);
      alert('Network error. Please check your connection and try again.');
    }
  };

  // Send SMS to patient
  const sendPatientSMS = async () => {
    if (!meetingLinks || !formData.mobileNumber) {
      alert('Patient mobile number not available');
      return;
    }

    setSmsStatus('sending');
    try {
      // Mock SMS API call - replace with actual SMS service
      const response = await fetch('/api/send-sms', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          phoneNumber: formData.mobileNumber,
          message: `Your video call link for insurance claim verification: ${meetingLinks.patientLink}`,
          claimId: formData.claimNumber
        })
      });

      if (response.ok) {
        setSmsStatus('sent');
        setTimeout(() => setSmsStatus('idle'), 3000);
      } else {
        setSmsStatus('error');
        setTimeout(() => setSmsStatus('idle'), 3000);
      }
    } catch (error) {
      console.error('Error sending SMS:', error);
      setSmsStatus('error');
      setTimeout(() => setSmsStatus('idle'), 3000);
    }
  };

  // Join doctor video call
  const joinDoctorCall = () => {
    if (!meetingLinks) return;

    setVideoCallStatus('pending');
    const videoCallWindow = window.open(
      meetingLinks.doctorLink,
      '_blank',
      'width=1200,height=800,scrollbars=yes,resizable=yes'
    );

    if (!videoCallWindow) {
      alert('Please allow popups for this site to open the video call window.');
      setVideoCallStatus('links-generated');
    }
  };

  // Copy link to clipboard
  const copyToClipboard = async (text: string, type: string) => {
    try {
      await navigator.clipboard.writeText(text);
      alert(`${type} link copied to clipboard!`);
    } catch (error) {
      console.error('Failed to copy:', error);
      alert('Failed to copy link. Please select and copy manually.');
    }
  };

  const handleFormSubmit = () => {
    // Check required fields
    const requiredFields = ['claimNumber', 'patientName', 'mobileNumber', 'policyNumber'];
    const missingFields = requiredFields.filter(
      (field) => !formData[field as keyof typeof formData]
    );

    if (missingFields.length > 0) {
      alert(
        `Please fill in the following required fields: ${missingFields.join(', ')}`
      );
      return;
    }

    completeStep(2);
    setCurrentStep(3);
  };

  const handleFinalSubmit = async () => {
    try {
      if (!sessionId) {
        alert('Session ID is missing. Please restart the process.');
        return;
      }

      const submissionData = {
        session_id: sessionId,

        // Required fields by API
        full_name: formData.patientName, // mapping to patientName
        email: formData.email || "",     // add email in your form
        phone: formData.mobileNumber,

        // Patient Details
        claim_number: formData.claimNumber,
        patient_name: formData.patientName,
        doj: formData.doj,
        mobile_number: formData.mobileNumber,
        policy_number: formData.policyNumber,
        product: formData.product,
        relationship: formData.relationship,
        insured_name: formData.insuredName,

        // Hospital Details
        hospital_name: formData.hospitalName,
        hospital_location: formData.hospitalLocation,
        hospital_state: formData.hospitalState,
        dod: formData.dod,
        nt_id: formData.ntId,
        hospital_status: formData.hospitalStatus,
        diagnosis_details: formData.diagnosisDetails,

        // Additional Information
        message: formData.message,

        // Meeting Links
        patient_meeting_link: meetingLinks?.patientLink,
        doctor_meeting_link: meetingLinks?.doctorLink,

        // Geolocation
        ...(geolocation && {
          latitude: geolocation.latitude,
          longitude: geolocation.longitude,
          geo_accuracy_m: geolocation.accuracy
        })
      };

      const response = await formsAPI.submit(submissionData);

      if (response.success) {
        completeStep(3);
        alert(
          'Claim verification request submitted successfully! You will receive a confirmation email shortly.'
        );
      } else {
        alert('Submission failed. Please try again.');
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('Network error. Please try again.');
    }
  };

  const handleInputChange = (
    e: React.ChangeEvent<
      HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement
    >
  ) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 py-8 px-4">
      <div className="max-w-5xl mx-auto">
        {/* ---------- Header ---------- */}
        {/* (same as your file, skipped for brevity) */}

        {/* ---------- Progress Bar ---------- */}
        {/* (same as your file, skipped for brevity) */}

        {/* ---------- Step Content ---------- */}
        <div className="bg-slate-800 rounded-2xl shadow-2xl p-8 mb-8 border border-slate-700">
          {/* Step 1: Video Meeting Setup */}
          {/* (same as your file, skipped for brevity) */}

          {/* Step 2: Claim Details Form */}
          {currentStep === 2 && (
            <div>
              <div className="text-center mb-8">
                <div className="w-24 h-24 bg-green-600 rounded-full flex items-center justify-center mx-auto mb-6">
                  <FileText size={48} className="text-white" />
                </div>
                <h2 className="text-3xl font-bold text-white mb-4">
                  Claim Verification Details
                </h2>
                <p className="text-slate-300">
                  Enter the claim information as per verification report
                </p>
              </div>

                <div className="max-w-4xl mx-auto space-y-8">
                  {/* Patient Details Section */}
                  <div className="bg-slate-700 rounded-xl p-6">
                    <h3 className="text-xl font-semibold text-purple-400 mb-6 border-b border-slate-600 pb-2">
                      Patient Details
                    </h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Claim Number *
                        </label>
                        <input
                          type="text"
                          name="claimNumber"
                          value={formData.claimNumber}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter claim number"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Patient Name *
                        </label>
                        <input
                          type="text"
                          name="patientName"
                          value={formData.patientName}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter patient name"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Date of Joining
                        </label>
                        <input
                          type="date"
                          name="doj"
                          value={formData.doj}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Mobile Number *
                        </label>
                        <input
                          type="tel"
                          name="mobileNumber"
                          value={formData.mobileNumber}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter mobile number"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Email *
                        </label>
                        <input
                          type="email"
                          name="email"
                          value={formData.email}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter email address"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Policy Number *
                        </label>
                        <input
                          type="text"
                          name="policyNumber"
                          value={formData.policyNumber}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter policy number"
                          required
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Product
                        </label>
                        <input
                          type="text"
                          name="product"
                          value={formData.product}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter product"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Relationship
                        </label>
                        <input
                          type="text"
                          name="relationship"
                          value={formData.relationship}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter relationship"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Insured Name
                        </label>
                        <input
                          type="text"
                          name="insuredName"
                          value={formData.insuredName}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter insured name"
                        />
                      </div>
                    </div>
                  </div>

                  {/* Hospital Details Section */}
                  <div className="bg-slate-700 rounded-xl p-6">
                    <h3 className="text-xl font-semibold text-purple-400 mb-6 border-b border-slate-600 pb-2">
                      Hospital Details
                    </h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Hospital Name
                        </label>
                        <input
                          type="text"
                          name="hospitalName"
                          value={formData.hospitalName}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter hospital name"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Hospital Location
                        </label>
                        <input
                          type="text"
                          name="hospitalLocation"
                          value={formData.hospitalLocation}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter hospital location"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Hospital State
                        </label>
                        <input
                          type="text"
                          name="hospitalState"
                          value={formData.hospitalState}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter hospital state"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Date of Discharge
                        </label>
                        <input
                          type="date"
                          name="dod"
                          value={formData.dod}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          NT ID
                        </label>
                        <input
                          type="text"
                          name="ntId"
                          value={formData.ntId}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter NT ID"
                        />
                      </div>
                      <div>
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Hospital Status
                        </label>
                        <input
                          type="text"
                          name="hospitalStatus"
                          value={formData.hospitalStatus}
                          onChange={handleInputChange}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter hospital status"
                        />
                      </div>
                      <div className="md:col-span-2">
                        <label className="block text-sm font-semibold text-slate-300 mb-2">
                          Diagnosis Details
                        </label>
                        <textarea
                          name="diagnosisDetails"
                          value={formData.diagnosisDetails}
                          onChange={handleInputChange}
                          rows={3}
                          className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                          placeholder="Enter diagnosis details"
                        />
                      </div>
                    </div>
                  </div>

                  {/* Additional Information */}
                  <div className="bg-slate-700 rounded-xl p-6">
                    <h3 className="text-xl font-semibold text-purple-400 mb-6 border-b border-slate-600 pb-2">
                      Additional Information
                    </h3>
                    <div>
                      <label className="block text-sm font-semibold text-slate-300 mb-2">
                        Additional Message
                      </label>
                      <textarea
                        name="message"
                        value={formData.message}
                        onChange={handleInputChange}
                        rows={4}
                        className="w-full px-4 py-3 bg-slate-800 border border-slate-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-200 text-white"
                        placeholder="Any additional information about the claim..."
                      />
                    </div>
                  </div>

                {/* Navigation Buttons */}
                <div className="flex justify-between pt-6">
                  <button
                    type="button"
                    onClick={() => setCurrentStep(1)}
                    className="px-6 py-3 border border-slate-600 text-slate-300 rounded-lg font-semibold hover:bg-slate-700 transition-colors duration-200"
                  >
                    Back
                  </button>
                  <button
                    type="button"
                    onClick={handleFormSubmit}
                    className="px-8 py-3 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 transition-colors duration-200 shadow-lg hover:shadow-xl"
                  >
                    Continue to Review
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Preview & Submit */}
          {/* (same as your file, skipped for brevity) */}
        </div>

        {/* Step Navigation + Progress Summary */}
        {/* (same as your file, skipped for brevity) */}

        {/* Session Info */}
        {/* (same as your file, skipped for brevity) */}
      </div>
    </div>
  );
};

export default MultiStepForm;
