import React, { useState } from 'react';
import { 
  ArrowLeft, 
  User, 
  Phone, 
  MapPin, 
  Globe, 
  Upload, 
  FileText,
  Loader2,
  CheckCircle,
  AlertCircle,
  X
} from 'lucide-react';

const CreateClaimForm = ({ onBack}) => {
  const [formData, setFormData] = useState({
    claimID: '',
    patientName: '',
    patientMobile: '',
    hospitalCity: '',
    hospitalState: '',
    patientLanguage: ''
  });

  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [errors, setErrors] = useState({});
  const [submitStatus, setSubmitStatus] = useState(null);

  const languages = [
    'English', 'Spanish', 'French', 'German', 'Italian', 'Portuguese',
    'Chinese', 'Japanese', 'Korean', 'Arabic', 'Hindi', 'Russian',
    'Dutch', 'Swedish', 'Norwegian', 'Danish', 'Finnish'
  ];

  const usStates = [
    'AL', 'AK', 'AZ', 'AR', 'CA', 'CO', 'CT', 'DE', 'FL', 'GA',
    'HI', 'ID', 'IL', 'IN', 'IA', 'KS', 'KY', 'LA', 'ME', 'MD',
    'MA', 'MI', 'MN', 'MS', 'MO', 'MT', 'NE', 'NV', 'NH', 'NJ',
    'NM', 'NY', 'NC', 'ND', 'OH', 'OK', 'OR', 'PA', 'RI', 'SC',
    'SD', 'TN', 'TX', 'UT', 'VT', 'VA', 'WA', 'WV', 'WI', 'WY'
  ];

  // Generate unique claim ID
  React.useEffect(() => {
    const generateClaimID = () => {
      const timestamp = Date.now();
      const random = Math.floor(Math.random() * 1000);
      return `CLM-${timestamp}-${random.toString().padStart(3, '0')}`;
    };

    setFormData(prev => ({
      ...prev,
      claimID: generateClaimID()
    }));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => {
      const validTypes = ['application/pdf', 'application/zip', 'application/x-zip-compressed'];
      return validTypes.includes(file.type) || file.name.toLowerCase().endsWith('.pdf') || file.name.toLowerCase().endsWith('.zip');
    });

    if (validFiles.length !== files.length) {
      setErrors(prev => ({
        ...prev,
        files: 'Only PDF and ZIP files are allowed'
      }));
      return;
    }

    setUploadedFiles(prev => [...prev, ...validFiles]);
    setErrors(prev => ({
      ...prev,
      files: ''
    }));
  };

  const removeFile = (index) => {
    setUploadedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.patientName.trim()) {
      newErrors.patientName = 'Patient name is required';
    }

    if (!formData.patientMobile.trim()) {
      newErrors.patientMobile = 'Patient mobile number is required';
    } else if (!/^\+?[\d\s\-\(\)]{10,}$/.test(formData.patientMobile)) {
      newErrors.patientMobile = 'Please enter a valid mobile number';
    }

    if (!formData.hospitalCity.trim()) {
      newErrors.hospitalCity = 'Hospital city is required';
    }

    if (!formData.hospitalState) {
      newErrors.hospitalState = 'Hospital state is required';
    }

    if (!formData.patientLanguage) {
      newErrors.patientLanguage = 'Patient language is required';
    }

    return newErrors;
  };

  const handleSubmit = async (e) => {
    e?.preventDefault?.();

    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }

    setIsSubmitting(true);
    setSubmitStatus(null);

    try {
      // Create FormData object for file upload
      const formDataToSubmit = new FormData();

      // Append form fields
      Object.keys(formData).forEach(key => {
        formDataToSubmit.append(key, formData[key]);
      });

      // Append files
      uploadedFiles.forEach(file => {
        formDataToSubmit.append('documents', file);
      });

      // Replace with your actual FastAPI endpoint
      const response = await fetch('/api/claims', {
        method: 'POST',
        body: formDataToSubmit
      });

      if (response.ok) {
        const result = await response.json();
        setSubmitStatus({ type: 'success', message: 'Claim created successfully!' });

        // Reset form after successful submission
        setTimeout(() => {
          if (onBack) onBack();
        }, 2000);
      } else {
        throw new Error('Failed to create claim');
      }
    } catch (error) {
      console.error('Error submitting claim:', error);
      setSubmitStatus({ 
        type: 'error', 
        message: 'Failed to create claim. Please try again.' 
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Header */}
      <header className="bg-slate-800/60 backdrop-blur-md border-b border-slate-700/50 shadow-2xl">
        <div className="container mx-auto px-6 py-5">
          <div className="flex items-center space-x-4">
            <button
              onClick={onBack}
              className="flex items-center space-x-2 px-4 py-2 bg-slate-700/50 text-gray-300 rounded-xl hover:bg-slate-600/50 transition-all duration-200"
            >
              <ArrowLeft className="w-5 h-5" />
              <span>Back to Dashboard</span>
            </button>
            <div className="w-12 h-12 bg-gradient-to-r from-purple-500 via-blue-500 to-cyan-500 rounded-xl flex items-center justify-center shadow-lg">
              <FileText className="w-7 h-7 text-white" />
            </div>
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-white via-purple-200 to-cyan-200 bg-clip-text text-transparent">
                Create New Claim
              </h1>
              <p className="text-gray-300 text-sm font-medium">Insurance Claims Verification System</p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="container mx-auto px-6 py-8">
        <div className="max-w-4xl mx-auto">
          {/* Status Messages */}
          {submitStatus && (
            <div className={`mb-6 p-4 rounded-xl border backdrop-blur-md ${
              submitStatus.type === 'success' 
                ? 'bg-green-500/20 border-green-500/50 text-green-200' 
                : 'bg-red-500/20 border-red-500/50 text-red-200'
            }`}>
              <div className="flex items-center space-x-3">
                {submitStatus.type === 'success' ? (
                  <CheckCircle className="w-5 h-5" />
                ) : (
                  <AlertCircle className="w-5 h-5" />
                )}
                <span className="font-medium">{submitStatus.message}</span>
              </div>
            </div>
          )}

          {/* Form Card */}
          <div className="bg-slate-800/60 backdrop-blur-md border border-slate-700/50 rounded-xl shadow-xl overflow-hidden">
            <div className="px-8 py-6 border-b border-slate-700/50 bg-slate-700/30">
              <h2 className="text-2xl font-bold text-white">Claim Information</h2>
              <p className="text-gray-300 text-sm mt-1">Fill in the details to create a new insurance verification claim</p>
            </div>

            <div className="p-8 space-y-6">
              {/* Claim ID (Read-only) */}
              <div>
                <label className="block text-sm font-semibold text-gray-300 mb-2">
                  Claim ID
                </label>
                <div className="relative">
                  <input
                    type="text"
                    value={formData.claimID}
                    readOnly
                    className="w-full px-4 py-3 bg-slate-700/30 border border-slate-600 rounded-xl text-white placeholder-gray-400 focus:outline-none cursor-not-allowed opacity-75"
                  />
                  <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
                    <FileText className="w-5 h-5 text-gray-400" />
                  </div>
                </div>
                <p className="text-xs text-gray-400 mt-1">Auto-generated claim identifier</p>
              </div>

              {/* Patient Name */}
              <div>
                <label className="block text-sm font-semibold text-gray-300 mb-2">
                  Patient Name *
                </label>
                <div className="relative">
                  <input
                    type="text"
                    name="patientName"
                    value={formData.patientName}
                    onChange={handleInputChange}
                    placeholder="Enter patient's full name"
                    className={`w-full px-4 py-3 pl-12 bg-slate-700/50 border rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-200 ${
                      errors.patientName ? 'border-red-500' : 'border-slate-600'
                    }`}
                  />
                  <div className="absolute left-3 top-1/2 transform -translate-y-1/2">
                    <User className="w-5 h-5 text-gray-400" />
                  </div>
                </div>
                {errors.patientName && (
                  <p className="text-red-400 text-xs mt-1">{errors.patientName}</p>
                )}
              </div>

              {/* Patient Mobile */}
              <div>
                <label className="block text-sm font-semibold text-gray-300 mb-2">
                  Patient Mobile Number *
                </label>
                <div className="relative">
                  <input
                    type="tel"
                    name="patientMobile"
                    value={formData.patientMobile}
                    onChange={handleInputChange}
                    placeholder="+1-555-0123"
                    className={`w-full px-4 py-3 pl-12 bg-slate-700/50 border rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-200 ${
                      errors.patientMobile ? 'border-red-500' : 'border-slate-600'
                    }`}
                  />
                  <div className="absolute left-3 top-1/2 transform -translate-y-1/2">
                    <Phone className="w-5 h-5 text-gray-400" />
                  </div>
                </div>
                {errors.patientMobile && (
                  <p className="text-red-400 text-xs mt-1">{errors.patientMobile}</p>
                )}
              </div>

              {/* Hospital Location */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-semibold text-gray-300 mb-2">
                    Hospital City *
                  </label>
                  <div className="relative">
                    <input
                      type="text"
                      name="hospitalCity"
                      value={formData.hospitalCity}
                      onChange={handleInputChange}
                      placeholder="Enter hospital city"
                      className={`w-full px-4 py-3 pl-12 bg-slate-700/50 border rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 transition-all duration-200 ${
                        errors.hospitalCity ? 'border-red-500' : 'border-slate-600'
                      }`}
                    />
                    <div className="absolute left-3 top-1/2 transform -translate-y-1/2">
                      <MapPin className="w-5 h-5 text-gray-400" />
                    </div>
                  </div>
                  {errors.hospitalCity && (
                    <p className="text-red-400 text-xs mt-1">{errors.hospitalCity}</p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-semibold text-gray-300 mb-2">
                    Hospital State *
                  </label>
                  <div className="relative">
                    <select
                      name="hospitalState"
                      value={formData.hospitalState}
                      onChange={handleInputChange}
                      className={`w-full px-4 py-3 pl-12 bg-slate-700/50 border rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-purple-500 appearance-none cursor-pointer transition-all duration-200 ${
                        errors.hospitalState ? 'border-red-500' : 'border-slate-600'
                      }`}
                    >
                      <option value="">Select state</option>
                      {usStates.map(state => (
                        <option key={state} value={state} className="bg-slate-800">
                          {state}
                        </option>
                      ))}
                    </select>
                    <div className="absolute left-3 top-1/2 transform -translate-y-1/2">
                      <MapPin className="w-5 h-5 text-gray-400" />
                    </div>
                  </div>
                  {errors.hospitalState && (
                    <p className="text-red-400 text-xs mt-1">{errors.hospitalState}</p>
                  )}
                </div>
              </div>

              {/* Patient Language */}
              <div>
                <label className="block text-sm font-semibold text-gray-300 mb-2">
                  Patient Language *
                </label>
                <div className="relative">
                  <select
                    name="patientLanguage"
                    value={formData.patientLanguage}
                    onChange={handleInputChange}
                    className={`w-full px-4 py-3 pl-12 bg-slate-700/50 border rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-purple-500 appearance-none cursor-pointer transition-all duration-200 ${
                      errors.patientLanguage ? 'border-red-500' : 'border-slate-600'
                    }`}
                  >
                    <option value="">Select language</option>
                    {languages.map(lang => (
                      <option key={lang} value={lang} className="bg-slate-800">
                        {lang}
                      </option>
                    ))}
                  </select>
                  <div className="absolute left-3 top-1/2 transform -translate-y-1/2">
                    <Globe className="w-5 h-5 text-gray-400" />
                  </div>
                </div>
                {errors.patientLanguage && (
                  <p className="text-red-400 text-xs mt-1">{errors.patientLanguage}</p>
                )}
              </div>

              {/* File Upload */}
              <div>
                <label className="block text-sm font-semibold text-gray-300 mb-2">
                  Supporting Documents
                </label>
                <div className="border-2 border-dashed border-slate-600 rounded-xl p-6 text-center hover:border-purple-500/50 transition-all duration-200">
                  <input
                    type="file"
                    multiple
                    accept=".pdf,.zip"
                    onChange={handleFileUpload}
                    className="hidden"
                    id="file-upload"
                  />
                  <label
                    htmlFor="file-upload"
                    className="cursor-pointer flex flex-col items-center space-y-3"
                  >
                    <div className="w-16 h-16 bg-purple-500/20 rounded-full flex items-center justify-center">
                      <Upload className="w-8 h-8 text-purple-400" />
                    </div>
                    <div>
                      <p className="text-white font-medium">Click to upload documents</p>
                      <p className="text-gray-400 text-sm">PDF and ZIP files only, up to 10MB each</p>
                    </div>
                  </label>
                </div>

                {errors.files && (
                  <p className="text-red-400 text-xs mt-1">{errors.files}</p>
                )}

                {/* Uploaded Files List */}
                {uploadedFiles.length > 0 && (
                  <div className="mt-4 space-y-2">
                    <p className="text-sm font-medium text-gray-300">Uploaded Files:</p>
                    {uploadedFiles.map((file, index) => (
                      <div key={index} className="flex items-center justify-between p-3 bg-slate-700/30 rounded-lg border border-slate-600">
                        <div className="flex items-center space-x-3">
                          <FileText className="w-5 h-5 text-purple-400" />
                          <div>
                            <p className="text-white text-sm font-medium">{file.name}</p>
                            <p className="text-gray-400 text-xs">{formatFileSize(file.size)}</p>
                          </div>
                        </div>
                        <button
                          type="button"
                          onClick={() => removeFile(index)}
                          className="p-1 hover:bg-red-500/20 rounded-full transition-colors duration-200"
                        >
                          <X className="w-4 h-4 text-red-400" />
                        </button>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Submit Buttons */}
              <div className="flex flex-col sm:flex-row gap-4 pt-6 border-t border-slate-700/50">
                <button
                  type="button"
                  onClick={onBack}
                  className="flex-1 px-6 py-3 bg-slate-600 text-white rounded-xl hover:bg-slate-700 transition-all duration-200 font-medium"
                >
                  Cancel
                </button>
                <button
                  type="button"
                  onClick={handleSubmit}
                  disabled={isSubmitting}
                  className="flex-1 flex items-center justify-center space-x-2 px-6 py-3 bg-gradient-to-r from-purple-600 to-purple-700 text-white rounded-xl hover:from-purple-700 hover:to-purple-800 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 font-medium shadow-lg hover:shadow-xl"
                >
                  {isSubmitting ? (
                    <>
                      <Loader2 className="w-5 h-5 animate-spin" />
                      <span>Creating Claim...</span>
                    </>
                  ) : (
                    <>
                      <CheckCircle className="w-5 h-5" />
                      <span>Create Claim</span>
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default CreateClaimForm;
